import React from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const Product = (props) => {
  const items = props.data.map((item) => {
    const { id, productName, image, price, inventory, icon } = item;
    const add = (itemId) => {};
