            <Card>
              <CardImg
                top
                style={{ width: "100%" }}
                src={image && image}
                alt="Card image cap"
              />
              <CardBody>
                <CardTitle>{productName}</CardTitle>
                <CardSubtitle>price: {price} â‚¬</CardSubtitle>
                <CardText></CardText>
                <Button onClick={add(id)} disabled={inventory === 0}>
                  {inventory > 0 ? "Add to cart" : "Sold out"}
                </Button>
              </CardBody>
            </Card>
          </Link>
        </li>
      </div>
    );
  });
  return (
    <React.Fragment>
      <h3>Products</h3>
      <ul className="Products">{items}</ul>
    </React.Fragment>
  );
};

export default Product;
